import React, { Component } from "react";
import axios from "axios";
import $ from "jquery";

import DataTable from "react-data-table-component";

class Lazada extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: [],
        };
        this.onSubmitHandle = this.onSubmitHandle.bind(this);
        // this.deleteProduct = this.deleteProductLazada.bind(this);
        // this.editProduct = this.editProductLazada.bind(this);
        // this.submitEditProduct = this.submitEditProductLazada.bind(this);
        this.handleChange = this.handleChange.bind(this);
    }

    async componentDidMount() {
        await axios.get("http://127.0.0.1:8000/api/get-productLazada").then((res) => {
            this.setState(() => ({ products: res.data }));
        });
    }

    previewImage() {
        $(document).ready(function (e) {
            $("#inputImage").change(function () {
                let reader = new FileReader();
                reader.onload = (e) => {
                    $("#preview-image-before-upload").attr("src", e.target.result);
                };
                reader.readAsDataURL(this.files[0]);
            });
        });
    }

    previewEditImage() {
        $(document).ready(function (e) {
            $("#editImage").change(function () {
                let reader = new FileReader();
                reader.onload = (e) => {
                    $("#preview-image-before-edit").attr("src", e.target.result);
                };
                reader.readAsDataURL(this.files[0]);
            });
        });
    }

    async onSubmitHandle(e) {
        e.preventDefault();

        const fd = new FormData();
        fd.append("uploadImage", this.state.fileUpload);

        if ($("#inputImage").val().split("\\")[2]) {
            await axios
                .post(`http://localhost:8000/api/upload-image`, fd)
                .then((res) => { });
        }

        await axios
            .post("http://localhost:8000/api/add-product", {
                name: $("#inputName").val(),
                image: $("#inputImage").val().split("\\")[2],
                price: $("#inputPrice").val(),
                shop: $("#inputShop").val(),
                
            })
            .then((res) => {
                $("#inputImage").val("");
                alert("Thêm thành công");
                $("#closeModalAddBtn").click();
                this.componentDidMount();
            })
            .catch(() => {
                alert("Thêm không thành công");
            });
    }

    render() {
        return (
            <div>
                {/* add productLazada */}
                <div>
                    <div
                        className="modal fade"
                        id="modelAddProduct"
                        tabIndex={-1}
                        role="dialog"
                        aria-labelledby="modelTitleId"
                        aria-hidden="true"
                    >
                        <div className="modal-dialog" role="document">
                            <div className="modal-content">
                                <div className="modal-header">
                                    <h5 className="modal-title">Modal Add Product</h5>
                                    <button
                                        type="button"
                                        className="close"
                                        data-dismiss="modal"
                                        aria-label="Close"
                                        id="closeModalAddBtn"
                                    >
                                        <span aria-hidden="true">X</span>
                                    </button>
                                </div>
                                <div className="modal-body">
                                    <form
                                        onSubmit={this.onSubmitHandle}
                                        encType="multipart/form-data"
                                    >
                                        <div className="form-group">
                                            <label htmlFor="inputName">Name</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                name="inputName"
                                                id="inputName"
                                                placeholder="Enter name"
                                                required
                                            />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="inputPrice">Price</label>
                                            <input
                                                type="number"
                                                min={10000}
                                                className="form-control"
                                                name="inputPrice"
                                                id="inputPrice"
                                                placeholder="Enter price"
                                                required
                                            />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="inputPromotionPrice">
                                                Promotion Price
                                            </label>
                                            <input
                                                type="number"
                                                min={10000}
                                                className="form-control"
                                                name="inputPromotionPrice"
                                                id="inputPromotionPrice"
                                                placeholder="Enter promotion price"
                                            />
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="inputUnit">Shop</label>
                                            <input
                                                type="text"
                                                className="form-control"
                                                name="inputUnit"
                                                id="inputUnit"
                                                placeholder="Enter unit"
                                                required
                                            />
                                        </div>
                                        
                                        <div className="form-group">
                                            <label htmlFor="inputImage">Image file</label>
                                            <input
                                                type="file"
                                                className="form-control-file"
                                                name="inputImage"
                                                id="inputImage"
                                                onChange={(e) => this.handleChange(e.target.files)}
                                                required
                                            />
                                        </div>
                                        <div className="form-group">
                                            <img
                                                id="preview-image-before-upload"
                                                src="https://www.riobeauty.co.uk/images/product_image_not_found.gif"
                                                alt="xem trước"
                                                style={{ maxHeight: 250 }}
                                            />
                                            {this.previewImage()}
                                        </div>
                                        <div className="form-group">
                                            <label htmlFor="inputDescription">Description</label>
                                            <input
                                                type="text"
                                                name="inputDescription"
                                                className="form-control"
                                                defaultValue={""}
                                            />
                                        </div>
                                        <button type="submit" className="btn btn-primary">
                                            Submit
                                        </button>
                                    </form>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }
}

export default Lazada;
